svg.script:

var KonusCount = 0;
var MundstueckCount = 0;
var TrichterCount = 0;

var KonusAbschnitt = document.getElementById("KonusAbschnitt");
var KonusRect = document.getElementById("KonusRect");
var KonusText = document.getElementById("KonusText");

var MundstueckAbschnitt = document.getElementById("MundstueckAbschnitt");
var MundstueckRect = document.getElementById("MundstueckRect");
var MundstueckText = document.getElementById("MundstueckText");

var TrichterAbschnitt = document.getElementById("TrichterAbschnitt");
var TrichterRect = document.getElementById("TrichterRect");
var TrichterText = document.getElementById("TrichterText");

var KonusButton = document.getElementById("KonusButton");
var MundstueckButton = document.getElementById("MundstueckButton");
var TrichterButton = document.getElementById("TrichterButton");

var KonusSchalter = document.getElementById("KonusSchalter");
var MundstueckSchalter = document.getElementById("MundstueckSchalter");
var TrichterSchalter = document.getElementById("TrichterSchalter");

var Konus = document.getElementById("Konus");
var Konus_ohne_Spitze = document.getElementById("Konus_ohne_Spitze");

var Roehre = document.getElementById("Roehre");

var Mundst_Blatt = document.getElementById("Mundst_Blatt");

var Trichter = document.getElementById("Trichter");

function addEventListeners() {

    KonusButton.addEventListener('mousedown', Konusdruck);
    KonusButton.addEventListener('mouseup', Konuslos);
    KonusButton.addEventListener('mouseout', Konusfort);

    MundstueckButton.addEventListener('mousedown', Mundstueckdruck);
    MundstueckButton.addEventListener('mouseup', Mundstuecklos);
    MundstueckButton.addEventListener('mouseout', Mundstueckfort);

    TrichterButton.addEventListener('mousedown', Trichterdruck);
    TrichterButton.addEventListener('mouseup', Trichterlos);
    TrichterButton.addEventListener('mouseout', Trichterfort);
}

addEventListeners()

function Konusdruck() {
    KonusSchalter.classList.remove("Bfrei");
    KonusSchalter.classList.add("Bgedrueckt");
}

function Konuslos() {
    KonusSchalter.classList.remove("Bgedrueckt");
    KonusSchalter.classList.add("Bfrei");
    if (KonusCount == 0) {
    KonusRect.style.fill = "#ffffff";
    KonusText.style.fill = "#ffffff";
    Roehre.classList.remove("unsichtbar");
    Roehre.classList.add("sichtbar");
    KonusCount = 1;
    }
    else {
    KonusRect.style.fill = "#99999a";
    KonusText.style.fill = "#99999a";
    Roehre.classList.remove("sichtbar");
    Roehre.classList.add("unsichtbar");
    KonusCount = 0;
    }
}

function Konusfort() {
    KonusSchalter.classList.remove("Bgedrueckt");
    KonusSchalter.classList.add("Bfrei");

}

function Mundstueckdruck() {
    MundstueckSchalter.classList.remove("Bfrei");
    MundstueckSchalter.classList.add("Bgedrueckt");
}

function Mundstuecklos() {
    MundstueckSchalter.classList.remove("Bgedrueckt");
    MundstueckSchalter.classList.add("Bfrei");
    if (MundstueckCount == 0) {
    MundstueckRect.style.fill = "#ff8080";
    MundstueckText.style.fill = "#ff8080";
    Mundst_Blatt.classList.remove("unsichtbar");
    Mundst_Blatt.classList.add("sichtbar");
    Konus.classList.remove("sichtbar");
    Konus.classList.add("unsichtbar");
    MundstueckCount = 1;
    }
    else {
    MundstueckRect.style.fill = "#99999a";
    MundstueckText.style.fill = "#99999a";
    Mundst_Blatt.classList.remove("sichtbar");
    Mundst_Blatt.classList.add("unsichtbar");
    Konus.classList.remove("unsichtbar");
    Konus.classList.add("sichtbar");
    MundstueckCount = 0;
    }
}

function Mundstueckfort() {
    MundstueckSchalter.classList.remove("Bgedrueckt");
    MundstueckSchalter.classList.add("Bfrei");

}


function Trichterdruck() {
    TrichterSchalter.classList.remove("Bfrei");
    TrichterSchalter.classList.add("Bgedrueckt");
}

function Trichterlos() {
    TrichterSchalter.classList.remove("Bgedrueckt");
    TrichterSchalter.classList.add("Bfrei");
    if (TrichterCount == 0) {
    TrichterRect.style.fill = "#89bbff";
    TrichterText.style.fill = "#89bbff";
    Trichter.classList.remove("unsichtbar");
    Trichter.classList.add("sichtbar");
    TrichterCount = 1;
    }
    else {
    TrichterRect.style.fill = "#99999a";
    TrichterText.style.fill = "#99999a";
    Trichter.classList.remove("sichtbar");
    Trichter.classList.add("unsichtbar");
    TrichterCount = 0;
    }
}

function Trichterfort() {
    TrichterSchalter.classList.remove("Bgedrueckt");
    TrichterSchalter.classList.add("Bfrei");

}
